class Solution {
	 public int totalMoney(int n) {
	if(n<=7)
	{
		return  (n*(n+1))/2;
	}
	int total=0;
	int sum= (7*(7+1))/2;
	int last=7;
	int count=1;
	while (n>7)
	{
		total+=sum;
		sum=sum-count;
		last+=1;
		count+=1;
		sum+=last;
		n=n-7;
	}
	while (n>0)
	{
		total+=count;
		count+=1;
		n--;
	}
	return total;
}

}
```

