class Solution {
public:
    TreeNode* increasingBST(TreeNode* root) {
        if (!root) {
            return nullptr;
        }

		// Make both child nodes an increasing order search tree
        TreeNode* left = increasingBST(root->left);
        TreeNode* right = increasingBST(root->right);

		// Disconnect the original left child node and connect to the right increasing order search tree
        root->left = nullptr;
        root->right = right;

		// Left child node returns a nullptr so just return the root
        if (!left) {
            return root;
        }

		// Otherwise we search for the rightmost child of the left increasing order search tree
        TreeNode* iter = left;
        while (iter && iter->right) {
            iter = iter->right;
        }

		// Connect to the root node
        iter->right = root;

		// Now the left root node should be the root of the whole tree
        return left;
    }
};
```

