    public static void inorder(TreeNode root){

        if(root==null) return;

        inorder(root.left);

        if(temp==null){
            TreeNode nn=new TreeNode(root.val);
            temp=nn;
            newroot=nn;
        }else{
            TreeNode nn1=new TreeNode(root.val);
            temp.right=nn1;
            temp=nn1;
        }

        inorder(root.right);

    }
}
