class Solution {
public:

    TreeNode *prevNode = NULL;

    TreeNode* increasingBST(TreeNode* root) 
    {
        if(!root) return NULL;

        root->right = increasingBST(root->right);

        if(prevNode)
            root->right = prevNode;

        TreeNode *temp = root->left;

        root->left = NULL;

        prevNode = root;

        increasingBST(temp);


        return prevNode;
    }
};
```

