var increasingBST = function(root) {
    let arr = []
    const getTree = (root) => {
        if(!root) return
        if (root.left) getTree(root.left)
        arr.push(root.val)
        if (root.right) getTree(root.right)

    }

    getTree(root)

    const buildTree = (arr) => {
        if (arr.length === 0) return null
        let tree = new TreeNode()
        tree.val = arr.shift()
        tree.right = buildTree(arr)
        return tree
    }

    return buildTree(arr)
};

