class Solution {
public:
    TreeNode* increasingBST(TreeNode* root) {
        if(!root)   return NULL;
        TreeNode* head = NULL;
        TreeNode* tail = head;
        stack<TreeNode*> st;

        while(!st.empty() || root) {
            if(root)    {
                st.push(root);
                root = root -> left;
            }
            else    {
                root = st.top();
                st.pop();
                if(!head)   {
                    head = new TreeNode(root->val);
                    tail = head;
                }
                else    {
                    tail -> right = new TreeNode(root -> val);
                    tail = tail -> right;
                }
                root = root -> right;
            }
        }
        return head;
    }
};
```

