class Solution {
    public TreeNode increasingBST(TreeNode root) {
        if (root == null) {
            return null;
        }
        TreeNode leftTree = increasingBST(root.left);
        if (leftTree != null) {
            TreeNode end = leftTree;
            while (end.right != null) {
                end = end.right;
            }
            end.right = root;
        } else {
            leftTree = root;
        }
        TreeNode rightTree = increasingBST(root.right);
        root.right = rightTree;
        root.left = null;
        return leftTree;
    }
}
