    public TreeNode increasingBST(TreeNode root) {
        List<TreeNode> list = new ArrayList<>();
        helper(root, list);

        TreeNode head = new TreeNode(-1);
        TreeNode dummy = head;
        for (TreeNode node : list){
            dummy.right = node;
            dummy = dummy.right;
            dummy.left = null;
        }
        return head.right;
