class Solution {
    public TreeNode increasingBST(TreeNode root) {

        if (root == null)        return null;

        LinkedList<TreeNode> stack = new LinkedList<>();
        TreeNode node = root;
        TreeNode prevNode = null;
        TreeNode head = null;

        while (!stack.isEmpty() || node != null){
            // go all the way to the left

            while(node != null){
                stack.push(node);
                node = node.left;
            }

            // get the current node
            // you can print or add to result for inorder
            node = stack.pop();

            if (head == null) head = node;
            if (prevNode != null){
                prevNode.right = node;
                node.left = null;
            }
            prevNode = node;
            // make the node point to the right
            node = node.right;
        }
        return head;
    }
}
