/**
 * Definition for a binary tree node.
 * public class TreeNode {
 *     int val;
 *     TreeNode left;
 *     TreeNode right;
 *     TreeNode() {}
 *     TreeNode(int val) { this.val = val; }
 *     TreeNode(int val, TreeNode left, TreeNode right) {
 *         this.val = val;
 *         this.left = left;
 *         this.right = right;
 *     }
 * }
 */
class Solution {
    //dummy goes down the TreeNode
    public TreeNode dummy = new TreeNode(0);
    public TreeNode increasingBST(TreeNode root) {
        //strategy: In Order Traversal
        TreeNode answer = dummy;
        //traverse each node
        helper(root);
        return answer.right;
    }
    public void helper(TreeNode root) {
        //not possible
        if (root == null) {
            return;
        }
        //first go to the left
        helper(root.left);
        //set up right and left
        dummy.right = new TreeNode(root.val);
        dummy.left = null;
        //move dummy to the right
        dummy = dummy.right;
        //traverse right
        helper(root.right);
    }
}

