public List<String> watchedVideosByFriends(List<List<String>> watchedVideos, int[][] friends, int id, int level) {
        int[] distance = new int[friends.length];
        Arrays.fill(distance,1000);
        distance[id] = 0;
        for(int m = 0;m<level;m++){
            for(int i = 0;i<friends.length;i++){
                for(int j = 0;j<friends[i].length;j++){
                    int idFriend = friends[i][j];
                    if(distance[idFriend] > distance[i] + 1){
                        distance[idFriend] = distance[i] + 1;
                    }
                }
            }
        }
        HashMap<String, Integer> map = new HashMap<>();
        for(int i = 0;i<distance.length;i++){
            if(distance[i] != level) continue;
            List<String> videoList = watchedVideos.get(i);
            for(String s: videoList){
                int count = map.getOrDefault(s,0);
                map.put(s,count+1);
            }
        }
        List<String> ans = new ArrayList<>(map.keySet());
        ans.sort((s1,s2) -> {
            if(map.get(s1) != map.get(s2)) return map.get(s1) - map.get(s2);
            else return s1.compareTo(s2);
        });
        return ans;
    }
