        int count=0;
        queue<int>q;
        q.push(id);
        vector<int>visited(n,0);
        visited[id]=1;
        q.push(-1);
        while(!q.empty())
        {
            int x=q.front();
            q.pop();
            if(x==-1)
            {
               count++;
               if(count==level)
               break;
                q.push(-1);
