class Solution {
    public List<String> watchedVideosByFriends(List<List<String>> watchedVideos, int[][] friends, int id, int level) {
        return getFriendsAtLevel(friends, id, level)
            .stream()
            .map(watchedVideos::get)
            .flatMap(List<String>::stream) 
            .collect(Collectors.groupingBy(Function.identity(), Collectors.counting()))
            .entrySet()
            .stream()
            .sorted(
                    Comparator.comparing(Map.Entry<String, Long>::getValue)
                              .thenComparing(Comparator.comparing(Map.Entry<String, Long>::getKey)))
            .map(Map.Entry<String,Long>::getKey)
            .collect(Collectors.toList());
    }

    private List<Integer> getFriendsAtLevel(int[][] friends, int id, int level) {
        final LinkedList<Integer> q = new LinkedList<>();
        q.offer(id);
        final boolean[] visited = new boolean[friends.length];
        visited[id] = true;
        while (level-- > 0) {
            int size = q.size();
            while (size-- > 0) {
                for (int f : friends[q.poll()]) {
                    if (!visited[f]) {
                        visited[f] = true;
                        q.offer(f);
                    }
                }
            }
        }
        return q;        
    }
}
