    set<int> ff; 

    void dfs(vector<int> adj[],int node,int parent,int level){
            if(level == 0){
                    ff.insert(node);
                    return;
            }
            for(int child : adj[node]){
                    if(child != parent){
                            dfs(adj,child,node,level-1);
                    }
            }

    }    

