class Solution {
public:
    set<int> ff; 

    void dfs(vector<int> adj[],int node,int parent,int level){
            if(level == 0){
                    ff.insert(node);
                    return;
            }
            for(int child : adj[node]){
                    if(child != parent){
                            dfs(adj,child,node,level-1);
                    }
            }

    }    

    vector<string> watchedVideosByFriends(vector<vector<string>>& watched, vector<vector<int>>& friends, int id, int level) {


            vector<int> adj[100];
            for(int i = 0;i<friends.size();i++){

                    for(auto a : friends[i]){
                            adj[i].push_back(a);
                    }
            }


            dfs(adj,id,id,level);
            map<string,int> mp;

            for(auto a : ff){
                    for(string s : watched[a])
                            mp[s]++;
            }

            vector<pair<int,string>> v;
            for(auto [a,b] : mp){
                    v.push_back({b,a});
            }
            sort(v.begin(),v.end());
            vector<string> ans;
            for(auto a : v)
                    ans.push_back(a.second);
            return ans;





    }
};
```

