class Solution {
public:
    vector<string> watchedVideosByFriends(vector<vector<string>>& nums, vector<vector<int>>& f, int id, int level) {
        vector<int> ans;
        vector<pair<int ,string>> res;
        vector<string> ans1;
        map<string,int> m;
        queue<pair<int,int>> q;
        q.push({id,level});
        vector<int> vis(f.size()+1,0);
        vis[id]=1;
        while(!q.empty())
        {
                int node = q.front().first;
                int x = q.front().second;
                q.pop();
                if(x==0)
                {
                   ans.push_back(node); 
                }
                for(int j=0;j<f[node].size();j++)     
                { 
                    if(vis[f[node][j]]==0)
                    {
                        q.push({f[node][j],x-1});
                        vis[f[node][j]]=1;
                    }                        
                }

        }
      for(auto it: ans)
      {
         for(int i=0;i<nums[it].size();i++)
         {
             m[nums[it][i]]++;
         }
      }
        for(auto it: m)
        {
            // cout<<it.first<<" "<<it.second<<endl;
            res.push_back({it.second,it.first});
        }
        sort(res.begin(),res.end());
        for(int i=0;i<res.size();i++)
        {
            ans1.push_back(res[i].second);
        }
        return ans1;
    }
};
