impl Solution {
    pub fn watched_videos_by_friends(
        watched_videos: Vec<Vec<String>>,
        friends: Vec<Vec<i32>>,
        id: i32,
        level: i32,
    ) -> Vec<String> {
        use std::collections::HashMap;

        let n = friends.len();
        let mut visited = vec![false; n];
        visited[id as usize] = true;
        let mut q = Vec::<i32>::new();
        q.push(id);

        for _ in 0..level {
            let mut q2 = Vec::<i32>::new();
            for v in q {
                for &w in &friends[v as usize] {
                    if !visited[w as usize] {
                        visited[w as usize] = true;
                        q2.push(w);
                    }
                }
            }
            q = q2;
        }

        let mut freq = HashMap::<String, i32>::new();
        for person in q {
            for s in &watched_videos[person as usize] {
                *freq.entry(s.to_owned()).or_default() += 1;
            }
        }

        let mut ans = Vec::<(i32, String)>::new();
        for (key, val) in freq {
            ans.push((val, key));
        }
        ans.sort();
        ans.iter().map(|x| x.1.to_owned()).collect()
    }
}

