class Solution {
    void helper(vector<vector<int>> friends , int level , int id, vector<int>& reqdFriends, unordered_map<int,int>& u){
        int i;
        u[id]++;
        if(level == 1){
            for(i = 0; i < friends[id].size(); i++){
                if(u.find(friends[id][i]) == u.end()){
                    //wont add friends found on previous level
                    reqdFriends.push_back(friends[id][i]);
                }
                u[friends[id][i]] ++;
            }
            return;
        }
        for(i = 0; i < friends[id].size() ; i++){
            u[friends[id][i]]++;// if reqd level is 3 and 0 has friend 1 and 1 has friend 2 and 2 has friend 1 then we wont count 1 as its zeroes 1 level friend so keeping track of friends
        }
        for(i = 0; i < friends[id].size() ; i++){
            helper(friends, level - 1, friends[id][i], reqdFriends, u);
        }
    }
