    vector<string> watchedVideosByFriends(vector<vector<string>>& watchedVideos, vector<vector<int>>& friends, int id, int level) {
        vector<int> reqdFriends;// friends whose interest is to be found
        vector<string> output;// my final output
        unordered_map<int,int> u;
        unordered_map<string,int> map;
        vector<MyPair> res;
        int i;
        helper(friends, level, id, reqdFriends, u);//to fill reqdFriends array
        for(i = 0; i < reqdFriends.size(); i++){
            if(reqdFriends[i] == id){
                continue;
            }
            for(int j = 0; j < watchedVideos[reqdFriends[i]].size(); j++){
                    map[watchedVideos[reqdFriends[i]][j]] ++;//keeping count of movies
            }
