        for(auto it : map){
            if(to_string(id) == it.first){
                continue;
            }
            MyPair m;
            m.movie = it.first;
            m.freq = it.second;
            // for ordering I am storing the movie and its freq in MyPair
            res.push_back(m);
        }
        sort(res.begin(), res.end(), compare);//sorting as needed
        for(i = 0 ; i < res.size(); i++){
            //inserting movies as asked
            output.push_back(res[i].movie);
        }
        return output;
    }
};

