class Solution {
public:
    ////////////////////////////////////////////// Bellman Ford /////////////////////////////////////////////////
    vector<string> watchedVideosByFriends(vector<vector<string>>& w, vector<vector<int>>& f, int id, int level) {
        set<int>ans;
        int n=f.size();
        vector<vector<int>>dp(level+1,vector<int>(n,INT_MAX));
        for(int i=0;i<level+1;i++){
            dp[i][id]=0;
        }
        for(int i=1;i<level+1;i++){
            for(int l=0;l<f.size();l++){
                int u=l;
                for(int m=0;m<f[l].size();m++){
                    int v=f[l][m];
                    if(dp[i-1][u]!=INT_MAX){
                        dp[i][v]=min(dp[i][v],dp[i-1][u]+1);
                    }
                }
            }
        }
        for(int i=0;i<=level;i++){
            for(int j=0;j<n;j++){
                if(dp[i][j]==level) ans.insert(j);
            }
        }
        map<string,int>m;
        vector<string>s;
        for(auto x:ans){
            for(auto y:w[x]) m[y]++;
        }
        vector<pair<int,string>>temp;
        for(auto x:m){
            temp.push_back({x.second,x.first});
        }
        sort(temp.begin(),temp.end());
        for(int i=0;i<temp.size();i++){
            s.push_back(temp[i].second);
        }
        return s;
    }

    ////////////////////////////////////////////////// BFS /////////////////////////////////////////////////////
    vector<string> watchedVideosByFriendsBFS(vector<vector<string>>& w, vector<vector<int>>& f, int id, int l) {
        int n=f.size();
        queue<int>q;
        q.push(id);
        int x=0;
        vector<int>dist(n,INT_MAX);
        dist[id]=0;
        while(!q.empty()){
            int y=q.front();q.pop();
            for(int i=0;i<f[y].size();i++){
                if(dist[f[y][i]]>dist[y]+1){
                    dist[f[y][i]]=dist[y]+1;
                    q.push(f[y][i]);
                }
            }
        }
        map<string,int>m;
        vector<string>s;
        for(int i=0;i<dist.size();i++){
            if(dist[i]==l){
                for(auto x:w[i]) m[x]++;
            }
        }
        vector<pair<int,string>>temp;
        for(auto x:m){
            temp.push_back({x.second,x.first});
        }
        sort(temp.begin(),temp.end());
        for(int i=0;i<temp.size();i++){
            s.push_back(temp[i].second);
        }
        return s;
    }
};
