class Solution {
    public List<String> watchedVideosByFriends(List<List<String>> watchedVideos, int[][] friends, int id, int level) {
        var graph = constructGraph(watchedVideos, friends);

        return breadthFirstSearch(graph, id, level);
    }

    private static List<String> breadthFirstSearch(Graph graph, int id, int level) {
        var watchedVideosByPeopleToWatchCountMap = new HashMap<String, Integer>();

        var bfsQueue = new LinkedList<Integer>();
        bfsQueue.add(id);

        var visited = new boolean[graph.getSize()];

        var numberOfLevelsConsidered = 0;

        while (!bfsQueue.isEmpty()) {
            var queueSize = bfsQueue.size();

            for (var i = 0; i < queueSize; i++) {
                var currentPersonId = bfsQueue.remove();

                if (visited[currentPersonId]) {
                    continue;
                }

                visited[currentPersonId] = true;

                if (numberOfLevelsConsidered == level) {
                    var videosWatchedByCurrentPerson = graph.getVideosWatchedById(currentPersonId);

                    for (var videoWatchedByCurrentPerson : videosWatchedByCurrentPerson) {
                        if (!watchedVideosByPeopleToWatchCountMap.containsKey(videoWatchedByCurrentPerson)) {
                            watchedVideosByPeopleToWatchCountMap.put(videoWatchedByCurrentPerson, 1);
                        } else {
                            var videoWatchedByCurrentFriendCurrentWatchCount = watchedVideosByPeopleToWatchCountMap.get(videoWatchedByCurrentPerson);
                            watchedVideosByPeopleToWatchCountMap.put(videoWatchedByCurrentPerson, videoWatchedByCurrentFriendCurrentWatchCount + 1);
                        }
                    }
                }

                var friendIdsOfCurrentPerson = graph.getFriendsOfId(currentPersonId);

                bfsQueue.addAll(friendIdsOfCurrentPerson);
            }

            if (numberOfLevelsConsidered == level) {
                break;
            }

            numberOfLevelsConsidered++;
        }

        return sortWatchedVideosByPeople(watchedVideosByPeopleToWatchCountMap.entrySet());
    }

    private static Graph constructGraph(List<List<String>> watchedVideos, int[][] friends) {
        var graph = new Graph();

        for (var id = 0; id < friends.length; id++) {
            var friendsOfCurrentId = new ArrayList<Integer>();

            for (var friend = 0; friend < friends[id].length; friend++) {
                friendsOfCurrentId.add(friends[id][friend]);
            }

            graph.addFriendsOfId(id, friendsOfCurrentId);

            var videosWatchedByCurrentId = watchedVideos.get(id);

            graph.addVideosWatchedById(id, videosWatchedByCurrentId);
        }

        return graph;
    }

    private static List<String> sortWatchedVideosByPeople(Set<Map.Entry<String, Integer>> watchedVideosByPeopleToWatchCountMap) {
        var sortedWatchedVideosByPeopleToWatchCountMapAsList = new ArrayList<>(watchedVideosByPeopleToWatchCountMap.stream().toList());

        sortedWatchedVideosByPeopleToWatchCountMapAsList.sort((firstEntry, secondEntry) -> {
            var firstVideoName = firstEntry.getKey();
            var firstVideoWatchCount = firstEntry.getValue();
            var secondVideoName = secondEntry.getKey();
            var secondVideoWatchCount = secondEntry.getValue();

            if (Objects.equals(firstVideoWatchCount, secondVideoWatchCount)) {
                return firstVideoName.compareTo(secondVideoName);
            }

            if (firstVideoWatchCount > secondVideoWatchCount) {
                return 1;
            }

            return -1;
        });

        return new ArrayList<>(sortedWatchedVideosByPeopleToWatchCountMapAsList.stream().map(Map.Entry::getKey).toList());
    }

    private static class Graph {
        private final Map<Integer, List<Integer>> friendsById;

        private final Map<Integer, List<String>> videosWatchedById;

        public Graph() {
            this.friendsById = new HashMap<>();
            this.videosWatchedById = new HashMap<>();
        }

        public void addFriendsOfId(int id, List<Integer> friendsOfId) {
            this.friendsById.put(id, friendsOfId);
        }

        public void addVideosWatchedById(int id, List<String> videosWatchedById) {
            this.videosWatchedById.put(id, videosWatchedById);
        }

        public List<Integer> getFriendsOfId(int id) {
            return this.friendsById.get(id);
        }

        public List<String> getVideosWatchedById(int id) {
            return this.videosWatchedById.get(id);
        }

        public int getSize() {
            return this.friendsById.size();
        }
    }
}
