            var secondVideoName = secondEntry.getKey();
            var secondVideoWatchCount = secondEntry.getValue();

            if (Objects.equals(firstVideoWatchCount, secondVideoWatchCount)) {
                return firstVideoName.compareTo(secondVideoName);
            }

            if (firstVideoWatchCount > secondVideoWatchCount) {
                return 1;
            }

            return -1;
        });

        return new ArrayList<>(sortedWatchedVideosByPeopleToWatchCountMapAsList.stream().map(Map.Entry::getKey).toList());
    }

    private static class Graph {
        private final Map<Integer, List<Integer>> friendsById;

        private final Map<Integer, List<String>> videosWatchedById;

        public Graph() {
            this.friendsById = new HashMap<>();
            this.videosWatchedById = new HashMap<>();
        }

        public void addFriendsOfId(int id, List<Integer> friendsOfId) {
            this.friendsById.put(id, friendsOfId);
