class Solution {
    public List<String> watchedVideosByFriends(List<List<String>> watchedVideos, int[][] friends, int id, int level) {


        Set<Integer> result = new HashSet<>();

        result.add(id);

        Set<Integer> visited = new HashSet<>();
        visited.add(id);

        while(level > 0){

            List<Integer> temp = new ArrayList<>();

            for(int key: result){
                for(int friend: friends[key]){
                    if(!visited.contains(friend)){
                        temp.add(friend);
                    }
                }
            }

            level--;
            result.clear();
            result.addAll(temp);
            visited.addAll(temp);

        }

        Map<String, Integer> movies = new HashMap<>();

        for(int friend: result){
            for(String watchedVideo: watchedVideos.get(friend)){
                movies.put(watchedVideo, movies.getOrDefault(watchedVideo, 0 ) + 1);
            }
        }

        // System.out.println(movies);

        return movies.entrySet()
                        .stream()
                        .sorted((i1, i2) -> {
                            int compareResult = i1.getValue().compareTo(i2.getValue());
                            if(compareResult == 0){
                                return i1.getKey().compareTo(i2.getKey());
                            }
                            return compareResult;
                        })
                        .map(i -> i.getKey())
                        .collect(Collectors.toList());


    }
}
