```Java []
class Solution {
    public List<String> watchedVideosByFriends(List<List<String>> watchedVideos, int[][] friends, int id, int level) {
        LinkedList<Integer> queue = new LinkedList<Integer>();
        queue.offer(id);
        int layer = -1;
        int[] levels = new int[friends.length];
        Arrays.fill(levels, -1);
        while (!queue.isEmpty()) {
            layer++;
            int size = queue.size();
            for (int i = 0; i < size; i++) {
                int person = queue.poll();
                levels[person] = layer;
                queue.offer(person);
            }
            for (int i = 0; i < size; i++) {
                int person = queue.poll();
                for (int j = 0; j < friends[person].length; j++) {
                    if (levels[friends[person][j]] == -1) {
                        queue.offer(friends[person][j]);
                    }
                }
            }
            if (layer == level) {
                break;
            }
        }
        List<Integer> personList = new ArrayList<Integer>();
        for (int i = 0; i < levels.length; i++) {
            if (levels[i] == level) {
                personList.add(i);
            }
        }
        if (personList.size() == 0) {
            return new ArrayList<String>();
        }
        Map<String, Integer> frequencyMap = new HashMap<String, Integer>();
        for (Integer person : personList) {
            for (String video : watchedVideos.get(person)) {
                frequencyMap.put(video, 
                frequencyMap.getOrDefault(video, 0) + 1);
            }
        }
        Map<Integer, Set<String>> reverseMap = 
        new TreeMap<Integer, Set<String>>();
        for (String video : frequencyMap.keySet()) {
            reverseMap.putIfAbsent(frequencyMap.get(video), 
            new TreeSet<String>());
            reverseMap.get(frequencyMap.get(video)).add(video);
        }
        List<String> resultList = new ArrayList<String>();
        for (Integer frequency : reverseMap.keySet()) {
            for (String video : reverseMap.get(frequency)) {
                resultList.add(video);
            }
        }
        return resultList;
    }
}
