from collections import Counter
class Solution:
    def watchedVideosByFriends(self, watchedVideos: List[List[str]], friends: List[List[int]], id: int, level: int) -> List[str]:
        graph, visited, dist = {}, [], []
        n, mx = len(watchedVideos), float("inf")

        for i in range(n):
            graph[i] = [] 
            visited.append(0)
            dist.append(mx)

        for i in range(n):
            for neighbor in friends[i]:
                graph[i].append(neighbor)
                graph[neighbor].append(i)

        queue, idx = [id], 0
        visited[id], dist[id], total = 1, 0, []
        while idx < len(queue):
            src = queue[idx]
            idx += 1
            for neighbor in graph[src]:
                if not visited[neighbor]:
                    visited[neighbor] = 1
                    queue.append(neighbor)
                    dist[neighbor] = dist[src] + 1

        for i in range(n):
            if dist[i] == level:
                total += watchedVideos[i]

        freq, unique, combined = Counter(total), set(), {}
        for i in freq.keys():
            unique.add(freq[i])
            if combined.get(freq[i]) == None:
                combined[freq[i]] = [i]
            else:
                combined[freq[i]].append(i)

        for i in combined.keys():
            combined[i].sort()
        unique, ans = list(unique), []
        unique.sort()

        for i in unique:
            ans += combined[i]
        return ans

```

