    def watchedVideosByFriends(self, w: List[List[str]], friends: List[List[int]], id: int, level: int) -> List[str]:
        S = {id}
        q = [id]

        for _ in range(level):
            p = q
            q = []

            for u in p:
                for v in friends[u]:
                    if v not in S:
                        q.append(v)
                        S.add(v)

        L = []

        for i in q:
            L.extend(w[i])

        D = Counter(L)
        return [x[0] for x in sorted(D.items(), key = itemgetter(1, 0))]

