    vector<string> watchedVideosByFriends(vector<vector<string>>& wat, vector<vector<int>>& f, int id, int l) {
        int n=f.size();
        vector<int> adj[n];
        for(int i=0; i<n; i++){
            for(int j=0; j<f[i].size(); j++){
                int x=i;
                int y=f[i][j];
                adj[x].push_back(y);
                adj[y].push_back(x);
            }
        }
        queue<int> q;
        vector<int> vis(n,0);
        vector<int> lvl(n,0);
        q.push(id);
        lvl[id]=0;
        vis[id]=1;
        map<string,int> m;
        while(!q.empty()){
