# Code
```
func watchedVideosByFriends(watchedVideos [][]string, friends [][]int, id int, level int) []string {
    n := len(friends)
    visited := make([]int, n)
    freq := make(map[string]int)
    queue := make([]int, 0, 10)
    queue = append(queue, friends[id]...)
    visited[id] = 1
    for _, f := range friends[id] {
        visited[f] = 1
    }
    for len(queue) > 0 && level > 0 {
        level--
        for i := len(queue)-1; i >= 0; i-- {
            f := queue[0]
            queue = queue[1:]
            if level == 0 {
                for _, v := range watchedVideos[f] {
                    freq[v]++
                }
            }
            for _, fof := range friends[f] {
                if visited[fof] == 0 {
                    queue = append(queue, fof)
                    visited[fof] = 1
                }
            } 
        }  
    }
    ans := make([]string, 0, len(freq))
    for k, _ := range freq {
        ans = append(ans, k)
    }
    sort.Slice(ans, func(i, j int) bool {
        if freq[ans[i]] == freq[ans[j]] {
            return ans[i] < ans[j]
        }
        return freq[ans[i]] < freq[ans[j]]
    })
    return ans
}
