class Solution:
    def watchedVideosByFriends(self, watchedVideos: List[List[str]], friends: List[List[int]], id: int, level: int) -> List[str]:

        graph = defaultdict(list)
        for i, f in enumerate(friends):
            graph[i] = f

        levels = {i: None for i in range(len(graph))}
        levels[id] = 0

        queue = deque([id])
        k = 0

        while queue and k <= level:
            curr_node = queue.popleft()

            for next_node in graph[curr_node]:
                if levels[next_node] is None:
                    k = levels[curr_node] + 1
                    levels[next_node] = levels[curr_node] + 1
                    queue.append(next_node)

        levels = [k for k in levels if levels[k] == level]

        videos = defaultdict(int)
        for i in levels:
            for video in watchedVideos[i]:
                videos[video] += 1

        return sorted(videos, key=lambda x: (videos[x], x))









