# Intuition
1. create a list about target level friends
2. acording to friends list create a set of vedios with it freq
3. return vedios set

# Approach
1. use BFS to traverse friends tree, id as root.
2. put vedios into a prepared array and sort.
3. return

# Complexity
- Time complexity: O(n)

- Space complexity: O(n)

# Code
```
/**
 * Note: The returned array must be malloced, assume caller calls free().
 */
#define SIZE 100

int queue[SIZE];
int rear = 0;
int front = 0;

typedef struct vlist {
    char name[9];
    int freq;
} vlist_t;

int isEmpty() {
    return rear == front ? 1 : 0;
}

int isFull() {
    int ret;
    if (front == 0) {
        if (rear == (SIZE - 1))
            ret = 1;
        else
            ret = 0;
    } else {
        if (rear == (front - 1))
            ret = 1;
        else 
            ret = 0;
    }
    return  ret;
}

int dequeue() {
    int ret;
    if (isEmpty()) {
        printf("Queue is Empty!!\n");
        return -1;
    } else {
        ret = queue[front++];
        front %= SIZE;
        return ret;
    }
}

void enqueue(int input) {
    if (isFull()) {
        printf("Queue is Full, Something Wrong!!\n");
    } else {
        queue[rear++] = input;
        rear %= SIZE;
    }
}

int size() {
    int ret = rear >= front ? rear - front : SIZE - front + rear;
    return ret;
}

int cmpfunc (const void * a1, const void * a2) {
    vlist_t *x = (vlist_t *)a1;
    vlist_t *y = (vlist_t *)a2;
    if (x->freq != y->freq) {
        return x->freq - y->freq;
    } else {
        int i = 0;
        while (x->name[i] == y->name[i]) {
            ++i;
        }
        return (int)(x->name[i] - y->name[i]);
    }
}

char ** watchedVideosByFriends(char *** watchedVideos, int watchedVideosSize, int* watchedVideosColSize, int** friends, int friendsSize, int* friendsColSize, int id, int level, int* returnSize){
    char **ret;
    int target, fid, vcount;
    int *visited;
    vlist_t *vlist;

    visited = calloc(sizeof(int), friendsSize);
    vlist = calloc(sizeof(vlist_t), friendsSize * 100);
    enqueue(id);
    visited[id] = 1;

    // push target level to queue
    while (level > 0 && !isEmpty()) {
        int end = size();
        for (int i = 0; i < end; ++i) {
            target = dequeue();
            for (int j = 0; j < friendsColSize[target]; ++j) {
                fid = friends[target][j];
                if (!visited[fid]) {
                    visited[fid] = 1;
                    enqueue(fid);
                }
            }
        }
        level--;
    }
    free(visited);
    vcount = 0;
    while (!isEmpty()) {
        int id = dequeue();
        char vname[9];
        // printf("process id:%d\n", id);
        for (int i = 0; i < watchedVideosColSize[id]; ++i) {
            strcpy(vname, watchedVideos[id][i]);
            int checkin = 1;
            for (int j = 0; j < vcount; ++j) {
                if (!strcmp(vname, vlist[j].name)) {
                    checkin = 0;
                    vlist[j].freq += 1;
                    break;
                }
            }
            if (checkin) {
                strcpy(vlist[vcount].name, vname);
                vlist[vcount].freq = 1;
                vcount++;
            }
        }
    }
    qsort(vlist, vcount, sizeof(vlist_t), cmpfunc);

    *returnSize = vcount;
    ret = malloc(sizeof(char*) * (vcount));
    for (int i = 0; i < vcount; ++i) {
        int strsize = strlen(vlist[i].name)+1;
        ret[i] = malloc(sizeof(char) * strsize);
        strcpy(ret[i], vlist[i].name);
    }
    free(vlist);
    return ret;
}
