class Solution {
    class pair{
        int vertex;
        int l;
        pair(int vertex,int l){
            this.vertex=vertex;
            this.l=l;
        }
    }
    public List<String> watchedVideosByFriends(List<List<String>> watchedVideos, int[][] friends, int id, int level) {
        ArrayList<ArrayList<Integer>> adj = new ArrayList<>();
        for (int i = 0; i < watchedVideos.size(); i++) {
            adj.add(new ArrayList<>());
        }
        for(int i=0;i< friends.length;i++) {
            for (int j = 0; j < friends[i].length; j++) {
                adj.get(i).add(friends[i][j]);
            }
        }

        ArrayList<String>ans=new ArrayList<>();
        HashSet<Integer>hs=new HashSet<>();
        Queue<pair>q=new ArrayDeque<>();
        Map<String, Integer> videoFreq = new HashMap<>();
        q.add(new pair(id,0));
        while(q.size()>0){
            pair p=q.poll();
            if(hs.contains(p.vertex)){
                continue;
            }
            hs.add(p.vertex);
            if(p.l==level){
                for(String s:watchedVideos.get(p.vertex)){
                    videoFreq.put(s, videoFreq.getOrDefault(s,0)+1);
                }
            }
            else{
                for(int i: adj.get(p.vertex)){
                    if(!hs.contains(i)){
                        q.add(new pair(i,p.l+1));
                    }
                }
            }
        }
        List<Map.Entry<String, Integer>> videoFreqList= new ArrayList<>(videoFreq.entrySet()); // Convert map to a list
