class Solution {
public:
    static bool comp(pair<string, int> &p1, pair<string, int> &p2)
    {
        if(p1.second == p2.second)return p1.first < p2.first;
        return p1.second < p2.second;
    }
    vector<string> watchedVideosByFriends(vector<vector<string>>& watchedVideos, vector<vector<int>>& friends, int id, int level) {
        map<string, int> ans;
        vector<bool> IsVisited(watchedVideos.size(), false);
        IsVisited[id] = true;
        priority_queue<pair<int,int> , vector<pair<int,int> >, greater<pair<int,int> >> pq;
        pq.push({id, 0});
        while(!pq.empty())
        {
            int a = pq.top().first;
            int lvl = pq.top().second;
            pq.pop();
            if(lvl != level)
            {
                for(auto &x : friends[a])
                {
                    if(!IsVisited[x])
                     {
                        IsVisited[x] = true;
                        pq.push({x, lvl+1});
                        if(lvl+1 == level)for(auto &y : watchedVideos[x])ans[y]++;
                     }
                }
            }
        }
        vector<pair<string, int>> sol;
        for(auto &x : ans)sol.push_back(x);
        sort(sol.begin(),sol.end(),comp);
        vector<string> vdo;
        for(auto &x : sol)vdo.push_back(x.first);
        return vdo;
    }
};
