  vector<string> watchedVideosByFriends(vector<vector<string>>& watchedVideos, vector<vector<int>>& friends, int id, int level) {
        queue<int> pq;
        int n = friends.size();
        vector<int> vis(n, 0);
        vis[id]=1;
        int st = id;
        pq.push(st);
        int sz = 1;
        int lv = 1;

        map<string, int> mp;
        vector<string> ans;

        while (!pq.empty()) {

            for (int i = 0; i < sz; ++i) {
                auto x = pq.front();
                pq.pop();


                for (auto el : friends[x]) {
                    if (!vis[el]) {
                        vis[el] = true;
                        if (lv == level) {

                            for(auto c:watchedVideos[el]){
