class Solution {
public:
    vector<string> watchedVideosByFriends(vector<vector<string>>& watch_v, vector<vector<int>>& friends, int id, int k) {
        queue<int> q;
        q.push(id);
        int s=1;
        vector<bool> visited(friends.size(),0);
        visited[id]=1;
        while(q.size() && k--){
            s=q.size();
            while(s--){
                int node=q.front();
                q.pop();
                for(auto& f: friends[node]){
                    if(visited[f]==0){
                        // cout<<f<<" ";
                        visited[f]=1;
                        q.push(f);
                    }
                }
            }
            s=q.size();
        }
        vector<string> ans;
        map<int,set<string>> mp;
        map<string,int> mp1;
        while(q.size()){
            int node=q.front();
            q.pop();
            for(auto& ele: watch_v[node]){
                mp1[ele]++;
                if(mp1[ele]-1) mp[mp1[ele]-1].erase(ele);
                mp[mp1[ele]].insert(ele);
            }
        }
        for(auto& ele: mp){
            for(auto i: ele.second){
                ans.push_back(i);
            }
        }
        return ans;
    }
};
