public class Solution
{
    public IList<string> WatchedVideosByFriends(IList<IList<string>> watchedVideos, int[][] friends, int id, int level)
    {
        var dijkstra = new Dijkstra();
        var freq = new Dictionary<string, int>();

        var levels = dijkstra.FindShortestDistance(friends, id);

        for(int i = 0; i < levels.Length; i++)
        {
            if (levels[i] == level)
            {
                foreach(var video in watchedVideos[i])
                {
                    if (!freq.ContainsKey(video))
                        freq[video] = 0;

                    freq[video]++;
                }
            }
        }

        var res = freq.OrderBy(item => item, Comparer<KeyValuePair<string, int>>.Create((a, b) =>
        {
            if (a.Value == b.Value)
                return a.Key.CompareTo(b.Key);
            return a.Value.CompareTo(b.Value);
        })).ToDictionary(x => x.Key, y => y.Value);

        return res.Keys.ToList();
    }
}

public class Dijkstra
{
    public int[] FindShortestDistance(int[][] edges, int node)
    {
        var n = edges.Length;
        var dist = new int[n];
        var visited = new bool[n];
        var pq = new PriorityQueue<int, int>();

        for (int i = 0; i < n; i++)
            dist[i] = int.MaxValue;

        dist[node] = 0;
        pq.Enqueue(node, 0);

        while (pq.Count > 0)
        {
            var temp = pq.Dequeue();
            if (visited[temp])
                continue;

            foreach (var vertex in edges[temp])
            {
                if (!visited[vertex])
                {
                    var distance = dist[temp] + 1;
                    if (distance < dist[vertex])
                    {
                        dist[vertex] = distance;
                        pq.Enqueue(vertex, distance);
                    }
                }
            }
        }

        return dist;
    }
}

