/**
 * @param {string[][]} watchedVideos
 * @param {number[][]} friends
 * @param {number} id
 * @param {number} level
 * @return {string[]}
 */
var watchedVideosByFriends = function(watchedVideos, friends, id, level) {

    const visited = new Set();
    const queue = [id];
    visited.add(id);
    let lvl = 0

    //traverse the list of friends using BFS and level order traversal so that
    //the last level of queue will equal all friends at "level" level
    while (queue.length && lvl < level){

        const length = queue.length;
        for (let i = 0; i < length; i ++){
            const current = queue.shift();

            for (let friend of friends[current]){
                if (visited.has(friend)) continue;
                visited.add(friend);
                queue.push(friend);
            }
        };
        lvl++;
    };

    //for the remaining friends in the queue, create a list of
    //all movies watched and the number of times they were watched
    const moviesAndNumWatched = {};
    for (let friend of queue){
        for (let mov of watchedVideos[friend]){
            if (!(mov in moviesAndNumWatched)){
                moviesAndNumWatched[mov] = 1;
            }else{
                moviesAndNumWatched[mov] ++;
            };
        };
    };

    //convert the list of moviesAndNumWatched into an array where
    //the index in the array represents the number of times watched and
    //each element in the array shows all (unsorted) movies watched that number of times
    const array = [];
    for (let vid in moviesAndNumWatched){
        const index = moviesAndNumWatched[vid];
        if (!array[index]){
            array[index] = [];
        }
        array[index].push(vid);

    };

    //now sort each element in the array, take every video within that element and
    //push that video into the final array
    const final = [];
    for (let item of array){
        if (item){
            const sorted = (item.sort())
            for (let vid of sorted){
                final.push(vid);
            };
        };
    };
    return (final)
};
