class Solution {
    public List<String> watchedVideosByFriends(List<List<String>> watchedVideos, int[][] friends, int id, int level) {
        Map<Integer, Set<Integer>> graph = new HashMap<>();  
        Map<String,Integer> freqMap = new TreeMap<>();

        Queue<Integer> q = new LinkedList<>(); 
        Set<Integer> v = new HashSet<>(); 
        q.offer(id);  
        int lvl = level;

        while(!q.isEmpty() && lvl>0) {  
            lvl--;
            int size = q.size();  
            for(int i =  0; i < size; i++) { 
                Integer curr = q.poll();   
                if(v.add(curr)) {  
                    for(Integer child : friends[curr]) {  
                        if(!v.contains(child)) q.offer(child);
                    }
                }
            } 
        } 


        Set<Integer> qSet = new HashSet<>(q); 
        for(Integer p : qSet) { 
            if(v.contains(p)) continue; 
            List<String> wv = watchedVideos.get(p);  
            for(String video : wv) {  
                freqMap.put(video,(freqMap.containsKey(video) ? freqMap.get(video) : 0)+1);
            }
