class Solution {
    private final Set<Integer> visited = new HashSet<>();
    public List<String> watchedVideosByFriends(List<List<String>> watchedVideos, int[][] friends, int id, int level) {
        HashSet<Integer> friendsAtLevel = getFriendsAtLevel(id,level,friends);
        Map<String,Integer> videosFreq = getVideosFreq(friendsAtLevel,watchedVideos);
        return sortVideos(videosFreq);
    }

    private HashSet<Integer> getFriendsAtLevel(int start, int distance, int[][] friends) {
        Queue<Integer> queue = new ArrayDeque<>();
        queue.add(start);
        visited.add(start);
        while (!queue.isEmpty() && distance>0) {
            distance--;
            int size = queue.size();
            for (int i=0; i<size; i++) {
                int current = queue.remove();
                visited.add(current);
                for (int friend: friends[current])
                    if(!visited.contains(friend))
                        queue.add(friend);
            }
        }
        return new HashSet<>(queue);
    }

    private Map<String, Integer> getVideosFreq(HashSet<Integer> friendsAtLevel, List<List<String>> watchedVideos) {
        Map<String, Integer> videosFreq = new HashMap<>();
        for (int friend: friendsAtLevel) {
            if(visited.contains(friend)) continue;
            for (String video : watchedVideos.get(friend))
                videosFreq.put(video, videosFreq.containsKey(video) ? videosFreq.get(video) + 1 : 1);
        }
        return videosFreq;
    }

    private List<String> sortVideos(Map<String, Integer> videosFreq) {
        Map<Integer, List<String>> sortedVideos = new HashMap<>();
        for (Map.Entry<String,Integer> entry: videosFreq.entrySet()) {
            if(sortedVideos.containsKey(entry.getValue())) sortedVideos.get(entry.getValue()).add(entry.getKey());
            else {
                List<String> list = new ArrayList<>();
                list.add(entry.getKey());
                sortedVideos.put(entry.getValue(),list);
            }
        }
        List<String> result = new ArrayList<>();
        for (Integer freq: sortedVideos.keySet().stream().sorted().collect(Collectors.toCollection(LinkedHashSet::new))) {
            Collections.sort(sortedVideos.get(freq));
            result.addAll(sortedVideos.get(freq));
        }
        return result;
    }
}
