class Solution:
    def watchedVideosByFriends(self, watchedVideos: List[List[str]], friends: List[List[int]], id: int, level: int) -> List[str]:
        video_map = {}
        edge_map = {}
        watched_video_map = {}

        for i, vid_list in enumerate(watchedVideos):
            video_map[i] = vid_list
        for i, friend_list in enumerate(friends):
            edge_map[i] = set(friend_list)

        que = deque([id])
        visited = set({id})
        while len(que):
            if not level:
                break
            for _ in range(len(que)):
                friend = que.popleft()
                for nei in edge_map[friend]:
                    if nei not in visited:
                        que.append(nei)
                        visited.add(nei)
            level -= 1
        for _ in range(len(que)):
            friend = que.popleft()
            for vid in video_map[friend]:
                watched_video_map[vid] = watched_video_map.get(vid, 0) + 1
        sorted_vid = [item[0] for item in sorted(watched_video_map.items(), key=lambda x: (x[1], x[0]))]
        return sorted_vid
