public:
    vector<string> watchedVideosByFriends(vector<vector<string>>& watchedVideos, vector<vector<int>>& friends, int id, int level) {

        int i, j;
        int n = friends.size();
        vector<int> vis(n, 0);

        queue<pair<int, int>> q;
        q.push({id, 0});
        vis[id] = 1;

        vector<int> friend_ids;

        while(!q.empty()){
            pair<int, int> curr = q.front();
            q.pop();

            if(curr.second == level){
                friend_ids.push_back(curr.first);
            }

            for(auto it:friends[curr.first]){
                if(!vis[it]){
                    vis[it] = 1;
                    q.push({it, curr.second + 1});
                }
            }

        }

        unordered_map<string, int> m;
        for(i=0; i < friend_ids.size(); i++){
            for(auto it:watchedVideos[friend_ids[i]]){
                m[it]++;
            }
        }

        vector<pair<string, int>> v;
        for(auto it:m){
            v.push_back({it.first, it.second});
        }

        sort(v.begin(), v.end(), comp);

        vector<string> ans;

        for(i=0; i<v.size(); i++){
            ans.push_back(v[i].first);
        }

        return ans;


    }
};
