    public List<String> watchedVideosByFriends(List<List<String>> watchedVideos, int[][] friends, int id, int level) {
        Queue<Integer> q = new LinkedList<>();
        boolean visit[] = new boolean[friends.length];
        q.add(id);
        for(int i = 0;i < level;i++){
            int size = q.size();
            for(;size != 0;--size){
                int n = q.poll();
                visit[n] = true;
                for(var c:friends[n]){
                    if(!visit[c]){
                        q.add(c);
                        visit[c] = true;
