            for i in range(size):
                node = queue.pop(0)
                visit.add(node)
                for v in graph[node]:
                    if v not in visit:
                        queue.append(v)
                        visit.add(v)
            l +=1
            if l ==  level:
                break

        for q in queue:
            for w in watchedVideos[q]:
                count[w] +=1

        return sorted(count.keys(),key=lambda x:(count[x],x))
