class Solution:
    # Again, another BFS question
    # Just traverse friends level by level, record the videos watched when we reach k and return the sorted list
    def watchedVideosByFriends(self, watchedVideos: List[List[str]], friends: List[List[int]], id: int, level: int) -> List[str]:
        dq, n, l, hash_table = deque(), len(friends), 0, {}
        visited = [False for i in range(n)]
        dq.append(id)
        visited[id] = True
        while dq:
            for i in range(len(dq)):
                fd = dq.popleft()
                if l == level:
                    for vid in watchedVideos[fd]:
                        hash_table[vid] = hash_table.get(vid,0) + 1
                for nxt in friends[fd]:
                    if not visited[nxt]:
                        visited[nxt] = True
                        dq.append(nxt)
            if l == level:
                key_values = sorted([[k, v] for k, v in hash_table.items()], key = lambda x: [x[1], x[0]])
                return [x[0] for x in key_values]
            l += 1
