class Solution {
    public void bfs(List<List<String>> watchedVideos, int[][] friends, int id, int level, boolean[] visited, Map<String, Integer> videoFreq) {
        Deque<int[]> queue = new ArrayDeque<>();
        queue.addLast(new int[] {id,0}); // Storing id and level
        visited[id] = true; // Marking as visited
        while(!queue.isEmpty()){
            int[] personId = queue.pollFirst();
            if(personId[1]==level){ // If this is the required level increase all the watched moves frequency by 1 in map
                List<String> videos = watchedVideos.get(personId[0]);
                for(String video: videos){
                    videoFreq.put(video, videoFreq.getOrDefault(video,0)+1);
                }
            }
            else{ // No need to go to a level beyond required level
                for(int friend: friends[personId[0]]){ // BFS
                    if(!visited[friend]){ // If not visited the add it to queue
                        queue.addLast(new int[] {friend,personId[1]+1});
                        visited[friend] = true; // Marking as visited
                    }
                }
            }
        }
    }

    public List<String> watchedVideosByFriends(List<List<String>> watchedVideos, int[][] friends, int id, int level) {
        Map<String, Integer> videoFreq = new HashMap<>();
        boolean[] visited = new boolean[friends.length];
        bfs(watchedVideos, friends, id, level, visited, videoFreq);
        List<Map.Entry<String, Integer>> videoFreqList= new ArrayList<>(videoFreq.entrySet()); // Convert map to a list
        Collections.sort(videoFreqList, (e1, e2)-> e1.getValue()==e2.getValue()?e1.getKey().compareTo(e2.getKey()):e1.getValue()-e2.getValue()); // Sort based on frequency i.e. value if both are same then based on the movie i.e. key
        List<String> ans = new ArrayList<>();
        for(Map.Entry<String, Integer> e: videoFreqList){
            ans.add(e.getKey()); // Store all the movies in the asn list
         }
        return ans;
    }
}
