class Solution {
    public List<String> watchedVideosByFriends(List<List<String>> watchedVideos, int[][] friends, int id, int level) {
        int currlevel = 0;
        Queue<Integer> queue = new LinkedList<>();
        Set<Integer> hs = new HashSet<>();
        hs.add(id);
        for(int i = 0; i < friends[id].length; i++) {
            queue.offer(friends[id][i]);
            hs.add(friends[id][i]);
        }
        Map<String, Integer> freq = new HashMap<>();
        while(currlevel != level) {
            int size = queue.size();
            while(size > 0) {
                int polledId = queue.poll();
                if(currlevel + 1 == level) {
                    for(int i = 0; i < watchedVideos.get(polledId).size(); i++) {
                        freq.put(watchedVideos.get(polledId).get(i), freq.getOrDefault(watchedVideos.get(polledId).get(i), 0) + 1);
                    }
                } else {
                    // push into queue
                    for(int i = 0; i < friends[polledId].length; i++) {
                        if(!hs.contains(friends[polledId][i])) {
                            queue.offer(friends[polledId][i]);
                            hs.add(friends[polledId][i]);
                        } 
                    }
                }
                size--;
            }
            currlevel++;
        }

        Comparator<Map.Entry<String, Integer>> comparator = (e1, e2) -> {
            int i = e1.getValue().compareTo(e2.getValue());
            if (i == 0) {
                return e1.getKey().compareTo(e2.getKey());
            } else {
                return i;
            }
        };

        List<String> ans = freq.entrySet()
                .stream()
                .sorted(comparator)
                .map(e -> e.getKey())
                .collect(Collectors.toList());

        return ans;
    }
}
