class Solution {
    public List<String> watchedVideosByFriends(List<List<String>> watchedVideos, int[][] friends, int id, int level) {

        int n= friends.length;
        boolean[] visited= new boolean[n];
        List<Integer> vertexAtLevel= new ArrayList<>();

        //using bfs to compute the friends id at a certain level
        Queue<Integer> q= new LinkedList<>();
        q.add(id);
        visited[id]=true;

        int lvl=0;
        while(!q.isEmpty() && lvl<=level){

            int size= q.size();
            for(int j=0; j<size; j++){
                int vtx= q.poll();
                if(lvl== level){
                    //add into list
                    vertexAtLevel.add(vtx);
                }

                //add neighbours
                for(int i=0; i<friends[vtx].length; i++){
                    int nbr= friends[vtx][i];
                    if(!visited[nbr]){
                        q.add(nbr);
                        visited[nbr]= true;
                    } 
                }
            }
            lvl++;  
        }

        //fill watchedMovies in HashMap using vertexAtLevel list
        HashMap<String,Integer> map= new HashMap<>();
        for(int i=0; i<vertexAtLevel.size(); i++){
            List<String> movies= watchedVideos.get(vertexAtLevel.get(i));
            for(int j=0; j<movies.size(); j++){
                map.put(movies.get(j), map.getOrDefault(movies.get(j),0)+1);
            }
        }

        //add the hashMap movies in a PriorityQueue
        PriorityQueue<String> pq= new PriorityQueue<>(new Comparator<String>(){
            public int compare(String s1, String s2){

                int f1= map.get(s1);
                int f2= map.get(s2);
                if(f1==f2){
                    return s1.compareTo(s2);
                }else
                    return f1-f2;
            }
        });

        for(String movie: map.keySet())
            pq.add(movie);

        List<String> ans= new ArrayList<>();
        while(!pq.isEmpty())
            ans.add(pq.poll());

        return ans;

    }
}
