class Solution {
public:


    void bfs(int source, vector<vector<int>>& friends, vector<int>&level, vector<int>&vis)
    {
        queue<int>q;
        q.push(source);
        vis[source] = 1;
        level[source] = 0;
        while(!q.empty())
        {
            int curr = q.front();
            q.pop();
            for(auto child : friends[curr])
            {
                if(vis[child] == 1) continue;
                q.push(child);
                level[child] = level[curr]+1;
                vis[child] = 1;
            }
        }

    }



    vector<string> watchedVideosByFriends(vector<vector<string>>& watchedVideos, vector<vector<int>>& friends, int id, int level)       {
        vector<int>lvl(friends.size(), 0);
        vector<int>vis(friends.size(), 0);
        vector<string>ans;
        bfs(id, friends, lvl, vis);
        vector<int>selected;
        for(int i=0;i<lvl.size();i++)
        {
            if(lvl[i] == level)
            {
                selected.push_back(i);
            }
        }


//         for(int i=0;i<lvl.size();i++)
