class Solution {
public:
    vector<string> watchedVideosByFriends(vector<vector<string>>& watchedVideos, vector<vector<int>>& friends, int id, int level) {
        int n=watchedVideos.size();

        vector<bool> visited(n,false);
        map<string,int> watched;

        priority_queue<pair<int/*dist*/,int/*index*/>,vector<pair<int,int>>, std::greater<pair<int,int>>> q;
        q.push({0,id});

        while(!q.empty()) {
            auto t=q.top(); q.pop();

            int id=t.second;
            if(visited[id]) continue;
            visited[id]=true;       

            if(t.first==level)  {
              for(auto&w:watchedVideos[id])
                 watched[w]++;

                continue;
            }

            for(auto&f: friends[id])
             if(!visited[f])
               q.push({t.first+1,f});
        }

        vector<string> r;

        map<int,set<string>> ws;
        for(auto&s:watched) ws[s.second].insert(s.first);

        for(auto& p:ws)
            for(auto&k:p.second) r.push_back(k);

        return r;
    }
};
