class Solution {
	public List<String> watchedVideosByFriends(List<List<String>> watchedVideos, int[][] friends, int id, int level) {
		Map<String, Integer> videos = new HashMap<>();
		List<Integer> curLevel = new ArrayList<>();
		curLevel.add(id);
		Set<Integer> seen = new HashSet<>();
		seen.add(id);
		List<String> res = new ArrayList<>();
		for (int i = 0; i < level; i ++) {
			List<Integer> nextLevel = new ArrayList<>();
			for (int people: curLevel) {
				for (int friend: friends[people]) {
					if (!seen.contains(friend)) {
						nextLevel.add(friend);
						seen.add(friend);
					}
				}
			}
			curLevel = nextLevel;
		}
		for (int people: curLevel) {
			for (String watchedVideo: watchedVideos.get(people)) {
				videos.put(watchedVideo, videos.getOrDefault(watchedVideo, 0) + 1);
			}
		}
		for (String video: videos.keySet()) {
			res.add(video);
		}
		Collections.sort(res, (a, b) -> videos.get(a) == videos.get(b)? a.compareTo(b): videos.get(a) - videos.get(b));
		return res;
	}
}

